import React from "react";


const InputField = ({ label, name, type, value, onChange }) => {
  return (
    <div>
      <div class="form-group"></div>
      <label htmlFor={name}>{label}</label>
      <input
        type={type}
        id={name}
        name={name}
        value={value}
        onChange={onChange}
      />
    </div>
  );
};

export default InputField;
